refentry.refsect1:
  &reftitle.examples;



      Exponential of a vector


use SciPhp\NumPhp as np;

$m = np::expm1([0, 0.1, 0.01]);

echo "m\n$m";


      &example.outputs;


m
[0                  0.10517091807565   0.010050167084168]




      Exponential of a matrix


use SciPhp\NumPhp as np;

$m = np::diag([0.1, 0.05, 0.01]);

$r = np::expm1($m);

echo "m\n$m", "r\n$r";


      &example.outputs;


m
[[0.1   0     0   ]
 [0     0.05  0   ]
 [0     0     0.01]]
r
[[0.10517091807565   0                  0                ]
 [0                  0.051271096376024  0                ]
 [0                  0                  0.010050167084168]]
