refentry.refsect1:
&reftitle.parameters;




      start
      &number.function.parameters;


      stop
      &number.function.parameters;


      num
      &int.function.parameters; The number of values


      endpoint
      &bool.function.parameters; If True, $base ** $stop is the last element. Otherwise, it is not included. Default is True.


      base
      &number.function.parameters;

  &reftitle.examples;



      A range of 4 values including endpoint, in default base 10


<?php

use SciPhp\NumPhp as np;

$x = np::logspace(2, 5, 4);

echo $x;
?>


      &example.outputs;


[100     1000    10000   100000]




      A decreasing range of 4 values including endpoint, in default base 10


<?php

use SciPhp\NumPhp as np;

$x = np::logspace(5, 2, 4);

echo $x;
?>


      &example.outputs;


[100000  10000   1000    100   ]




      A range of 4 values excluding endpoint, in default base 10


<?php

use SciPhp\NumPhp as np;

$x = np::logspace(2, 5, 4, false);

echo $x;
?>


      &example.outputs;


[100              562.34132519035  3162.2776601684  17782.794100389]




      A range of 4 values including endpoint, in base 2


<?php

use SciPhp\NumPhp as np;

$x = np::logspace(2, 5, 4, true, 2);

echo $x;
?>


      &example.outputs;


[4   8   16  32]
